```python
class BankAccount:
    def __init__(self, account_number, account_holder, initial_balance=0):
        self.account_number = account_number
        self.account_holder = account_holder
        self.balance = initial_balance

    def deposit(self, amount):
        if amount > 0:
            self.balance += amount
            print(f"Средства в размере {amount} успешно зачислены. Текущий баланс: {self.balance}")
        else:
            print("Сумма пополнения должна быть положительной.")

    def withdraw(self, amount):
        if amount > 0:
            if self.balance >= amount:
                self.balance -= amount
                print(f"Средства в размере {amount} успешно сняты. Текущий баланс: {self.balance}")
            else:
                print("Недостаточно средств на счету.")
        else:
            print("Сумма снятия должна быть положительной.")

    def check_balance(self):
        print(f"Текущий баланс счета {self.account_number}: {self.balance}")

    def __str__(self):
        return f"Счет №{self.account_number}, владелец: {self.account_holder}, баланс: {self.balance}"


class Bank:
    def __init__(self):
        self.accounts = {}

    def create_account(self, account_number, account_holder, initial_balance=0):
        if account_number not in self.accounts:
            self.accounts[account_number] = BankAccount(account_number, account_holder, initial_balance)
            print(f"Счет {account_number} успешно создан для {account_holder}.")
        else:
            print("Счет с таким номером уже существует.")

    def get_account(self, account_number):
        return self.accounts.get(account_number, None)

    def main_menu(self):
        bank = self
        while True:
            print("\n===== Меню банка =====")
            print("1. Создать новый счет")
            print("2. Пополнить счет")
            print("3. Снять средства")
            print("4. Проверить баланс")
            print("5. Вывести информацию о счете")
            print("6. Выйти")
            choice = input("Выберите действие: ")

            if choice == '1':
                acc_num = input("Введите номер счета: ")
                acc_holder = input("Введите имя владельца счета: ")
                initial_bal = float(input("Введите начальный баланс (если нет, введите 0): "))
                bank.create_account(acc_num, acc_holder, initial_bal)

            elif choice == '2':
                acc_num = input("Введите номер счета для пополнения: ")
                account = bank.get_account(acc_num)
                if account:
                    amount = float(input("Введите сумму для пополнения: "))
                    account.deposit(amount)
                else:
                    print("Счет не найден.")

            elif choice == '3':
                acc_num = input("Введите номер счета для снятия: ")
                account = bank.get_account(acc_num)
                if account:
                    amount = float(input("Введите сумму для снятия: "))
                    account.withdraw(amount)
                else:
                    print("Счет не найден.")

            elif choice == '4':
                acc_num = input("Введите номер счета для проверки баланса: ")
                account = bank.get_account(acc_num)
                if account:
                    account.check_balance()
                else:
                    print("Счет не найден.")

            elif choice == '5':
                acc_num = input("Введите номер счета для просмотра информации: ")
                account = bank.get_account(acc_num)
                if account:
                    print(account)
                else:
                    print("Счет не найден.")

            elif choice == '6':
                print("До свидания!")
                break

            else:
                print("Некорректный выбор. Попробуйте снова.")


if __name__ == "__main__":
    bank_system = Bank()
    bank_system.main_menu()
```

